    private void nextButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_nextButtonActionPerformed
        // TODO add your handling code here:
        Error error = new Error();
        AddPackage addpack = new AddPackage();
        String name = nameTextField.getText();
        String description = descriptTextField.getText();
        String compatibility = compatibilityComboBox.getSelectedItem().toString();
        
        if (name.isEmpty() || description.isEmpty() || compatibility.equals("None")){
            error.showError("Please complete all required text fields!!");
        }
        else {
            try {
                addpack.showAddPackage(this.user, name, description, compatibility);
            } catch (Exception ex) {
                Logger.getLogger(NewPackage.class.getName()).log(Level.SEVERE, null, ex);
            }
            this.hide();
        }
    }//GEN-LAST:event_nextButtonActionPerformed

    public void showNewPackage(String user){
        this.user = user;
        putComboBoxBrand();
        this.setLocationRelativeTo(null); //center form in the screen
        this.setVisible(true);
        this.pack();
        this.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE); // deve s√≥ fechar a pagina....
    }
    
    public void putComboBoxBrand(){
        this.compatibilityComboBox.setModel(new javax.swing.DefaultComboBoxModel<>(new String[] {"None", "BMW", "Mini", "RollsRoyce"}));
    }
    
