    private void okbuttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_okbuttonActionPerformed
        // TODO add your handling code here:
        if(this.function.equals("Admin")){
            MenuAdmin menuadmin =  new MenuAdmin();
            try {
                menuadmin.showMenuAdmin(this.user);
                this.hide();
                this.jLabel2.setText("");
            } catch (Exception ex) {
                System.out.println("Error: " +ex.getMessage());
            }
        } else if(this.function.equals("Seller")){
            MenuSeller seller = new MenuSeller();
            try {
                seller.showMenuSeller(this.user);
                this.hide();
            }catch (Exception ex){
                System.out.println("Error: " +ex.getMessage());
            }   
        } else if(this.function.equals(("Employee"))){
            MenuEmployee emp = new MenuEmployee();
            try {
                emp.showMenuEmployee(this.user);
                this.hide();
            }catch (Exception ex){
                System.out.println("Error: " +ex.getMessage());
            }   
        }
    }//GEN-LAST:event_okbuttonActionPerformed

    public void showSuccess(String user, String function){
        this.user = user;
        this.function = function;
        this.setLocationRelativeTo(null); //center form in the screen
        this.setVisible(true);
        this.pack();
        this.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
    }
    

